/**
 * Sample React Native App
 * https://github.com/facebook/react-native
 *
 * @format
 */

import React from 'react';
import {
  SafeAreaView,
  ScrollView,
  StatusBar,
  StyleSheet,
  Text,
  useColorScheme,
  View,
} from 'react-native';

import {
  Colors,
  DebugInstructions,
  Header,
  LearnMoreLinks,
  ReloadInstructions,
} from 'react-native/Libraries/NewAppScreen';
import Routers from './routers/Routers';
import { ToastProvider } from 'react-native-toast-notifications'

function App() {
  return (
    <ToastProvider
      placement="top"
      duration={5000}
      animationType='zoom-in'
      animationDuration={250}
      successColor="#08bd38"
      dangerColor="#f72331"
      warningColor="#f79123"
      normalColor="gray"
      offset={50} // offset for both top and bottom toasts
      offsetTop={30}
      offsetBottom={40}
      renderType={{
        custom_type: (toast) => (
          <View style={{ width: '100%', padding: 15, backgroundColor: toast.data.type || 'red' }}>
            <Text>{toast.message}</Text>
          </View>
        )
      }}
      renderToast={(toast) => {
        console.log(toast.data.type);
        return (
          <View style={{ padding: 15, backgroundColor: toast.data.type }}>
            <Text style={{
              fontSize: 15,
              fontWeight: 'bold',
            }}>{toast.message}</Text>
          </View>
        )
      }}
      swipeEnabled={true}
    >
      <Routers />
    </ToastProvider>
  );
}

const styles = StyleSheet.create({
  sectionContainer: {
    marginTop: 32,
    paddingHorizontal: 24,
  },
  sectionTitle: {
    fontSize: 24,
    fontWeight: '600',
  },
  sectionDescription: {
    marginTop: 8,
    fontSize: 18,
    fontWeight: '400',
  },
  highlight: {
    fontWeight: '700',
  },
});

export default App;
