import React from 'react'
import { StyleSheet, Text, View } from 'react-native'

const globalStyles = StyleSheet.create({
    black: {
        color: "#333"
    },
    heading: {
        color: "#333",
        fontSize: 18,
        fontWeight: 'bold'
    },
    flex: {
        justifyContent: "space-between",
        alignItems: "center"
    },
    inputField: {
        backgroundColor: "#e7e7e7",
        borderRadius: 25,
        borderColor: "#333",
        padding: 15,
        elevation: 5
    },
    btnStyle: {
        borderRadius: 25,
    }
})


export default globalStyles;
